using Microsoft.AspNetCore.Mvc;
using MVCPractise.Models;
using MVCPractise.ViewModels;

namespace MVCPractise.Controllers
{
    public class HomeController : Controller
    {
        public IActionResult Index()
        {
            List<Product> products = new List<Product>
            {
                new Product{Image = "data:image/jpeg;base64,", Name = "Apple Watch", Description = "High Level", Price = 1000, DiscountPrice = 500, Category = "Watches", Color = "White"},
                new Product{Image = "data:image/jpeg;base64,",Name = "iPhone 15", Description = "High Level", Price = 1000, DiscountPrice = 500, Category = "Watches", Color = "Black"},
                new Product{Image = "data:image/jpeg;base64,",Name = "iPhone 16", Description = "High Level", Price = 1000, DiscountPrice = 500, Category = "Watches", Color = "Aqua"}
            };

            HomeVM homeVM = new HomeVM
            {
                Products = products
            };
            return View(homeVM);
        }
    }
}
